INSERT INTO `product_info` (`product_id`, `product_type`, `product_name`, `product_color`, `product_size`, `product_description`, `product_price`, `quantity`) VALUES
(1, 'T-SHIRT', 'ALIEN TEE', 'black', 'M,L,XL', 'ALIEN TEE\r\nMATERIAL: TICI SPANDEX\r\n\r\nSize và form áo được đo theo chuẩn của người Việt Nam\r\nNên chọn size lớn hơn nếu bạn thích mặc thoải mái\r\nSize M: Chiều cao từ 1m50 – 1m65, cân nặng dưới 60kg\r\nSize L: Chiều cao từ 1m65 – 1m72, cân nặng dưới 65kg\r\nSize XL: Chiều cao từ 1m70 – 1m77, cân nặng dưới 80kg\r\n\r\nBảng size nhằm mục đích hướng dẫn và khích thước có tỉ lệ dung size ± 0.5cm\r\nNếu bạn không chắc chắn về số đo của mình, hãy liên hệ chúng mình để được tư vấn kĩ hơn', 180000, 2),
(2, 'T-SHIRT', 'MONSTER 02 TEE', 'blue', 'M,L,XL', 'MATERIAL: TICI SPANDEX\r\n\r\nSize và form áo được đo theo chuẩn của người Việt Nam\r\nNên chọn size lớn hơn nếu bạn thích mặc thoải mái\r\nSize M: Chiều cao từ 1m50 – 1m65, cân nặng dưới 60kg\r\nSize L: Chiều cao từ 1m65 – 1m72, cân nặng dưới 65kg\r\nSize XL: Chiều cao từ 1m70 – 1m77, cân nặng dưới 80kg\r\n\r\nBảng size nhằm mục đích hướng dẫn và khích thước có tỉ lệ dung size ± 0.5cm\r\nNếu bạn không chắc chắn về số đo của mình, hãy liên hệ chúng mình để được tư vấn kĩ hơn', 180000, 999),
(3, 'T-SHIRT', 'PAINT TEE', 'green', 'M,L,XL', '', 180000, 1),
(6, 'HOODIE', 'BASIC HOODIE / NEON YELLOW', 'yellow', 'M,L', 'FLYING HEART TEE\r\nSize và form áo được đo theo chuẩn của người Việt Nam\r\nNên chọn size lớn hơn nếu bạn thích mặc thoải mái\r\nSize M: Chiều cao từ 1m50 – 1m65, cân nặng dưới 60kg\r\nSize L: Chiều cao từ 1m65 – 1m72, cân nặng dưới 65kg\r\nSize XL: Chiều cao từ 1m70 – 1m77, cân nặng dưới 80kg\r\nBảng size nhằm mục đích hướng dẫn và khích thước có tỉ lệ dung size ± 0.5cm Nếu bạn không chắc chắn về số đo của mình, hãy liên hệ chúng mình để được tư vấn kĩ hơn ', 160000, 1),
(7, 'HOODIE', 'HOODIE BASIC / WHITE', 'white', 'M,L', '', 179000, 1),
(8, 'HOODIE', 'HUMMING BIRD HOODIE / BLUE', 'blue', 'M,L', '', 129000, 1),
(9, 'HOODIE', 'SICKO® HOODIE', 'white', 'M,L', '', 300000, 1),
(10, 'SHORT PANTS', 'Quần shorts chạy bộ Ultra Fast & Free Run', 'black,blue', 'M,L,XL,2XL,3XL', 'Chất liệu: 100% Polyester\r\nXử lý hoàn thiện vải: Quick-Dry + Wicking + Stretch\r\nCông nghệ may Seamless hạn chế tối đa ma sát trong quá trình vận động\r\nPhù hợp với: chơi thể thao, chạy bộ. Được thiết kế dựa trên các Runner chuyên nghiệp\r\nĐộ dài quần: 5 inch\r\nTự hào sản xuất tại Việt Nam\r\nNgười mẫu: 183 cm - 76 kg, mặc quần 2XL', 279000, 1),
(11, 'SHORT PANTS', 'Quần short nam thể thao 7\" thoáng khí', 'black,blue', 'M,L,XL,2XL', 'Chất liệu: 100% sợi Recycled Polyester\r\nXử lý hoàn thiện vải: Quick-Dry + Wicking + Stretch\r\nVải Recycle dệt kiểu Double Weaving mang lại cảm giác Cooling khi mặc\r\nPhù hợp với: vận động thể thao\r\nĐộ dài quần: 7 inch\r\nTự hào sản xuất tại Việt Nam\r\nNgười mẫu: 186 cm - 72 kg, mặc quần size XL', 279000, 1),
(12, 'SHORT PANTS', 'Quần short nam thể thao 7\" co giãn công nghệ Graphene', 'gray', 'M,L,2XL,3XL', 'Chất liệu 100% sợi Polyester PTT mang lại độ co giãn tốt\r\nCông nghệ ứng dụng Graphene vào hoàn tất vải mang lại 6 tính năng ưu việt: Thấm hút, Nhanh khô, Kháng khuẩn, Chống UV, Cooling, Khử mùi.\r\nPhù hợp với: vận động thể thao\r\nĐộ dài quần: 7 inch\r\nNgười mẫu: 1m77 - 74kg, mặc quần size XL\r\nTự hào sản xuất tại Việt Nam', 249000, 1),
(13, 'SHORT PANTS', 'Quần shorts chạy bộ Advanced Fast & Free Run', 'black', 'M,L,XL,2XL', 'Chất liệu: 89% Recycled Polyester - 11% Spandex\r\nXử lý hoàn thiện vải: Quick-Dry + Wicking + Stretch\r\nCông nghệ Chafe-Free may Seamless hạn chế tối đa ma sát trong quá trình vận động\r\nĐịnh lượng vải 85gsm siêu nhẹ\r\nĐược phát triển và phù hợp dựa trên các Runner chuyên nghiệp\r\nĐộ dài quần: 5 inch\r\nTự hào sản xuất tại Việt Nam\r\nNgười mẫu: 183 cm - 76 kg, mặc quần 2XL', 399000, 1),
(14, 'SHORT PANTS', 'Quần short nam thể thao Movement 5\" co giãn', 'black', 'S,M,L,XL,2XL', 'Chất liệu: 100% Polyester kết hợp tính năng: Wicking (Thấm hút nhanh), 4-way Mechanical stretch (Co giãn 4 chiều) và Quick-Dry (Nhanh khô)\r\nVải siêu nhẹ chỉ 106 gsm, mang lại cảm giác mặc như không mặc\r\nVải được dệt kiểu Double Weaving và nhuộm bằng công nghệ Zero Water Discharge (Không nước thải) theo đuổi xu hướng yếu tố thời trang bền vững\r\nPhù hợp với: các hoạt động chạy nhẹ và tập gym\r\nĐộ dài quần: 5 inch\r\nTự hào sản xuất tại Việt Nam', 199000, 1),
(15, 'JEANS', 'Quần Jeans Clean Denim dáng Regular S2', 'dark blue', '29,30,31,32,33', 'Vải Denim được dệt toàn bộ tại nhà máy Nhơn Trạch, Đồng Nai của Saitex\r\nChất liệu: 99% Regenerative Cotton (Cotton Tái sinh) + 1% Elastane, tạo sự co giãn đem đến sự thoải mái khi vận động\r\nĐịnh lượng vải nhẹ chỉ với 12.5 Oz\r\nKéo sợi và dệt nhuộm theo quy trình sạch và bền vững tại Việt Nam\r\nKiểu dáng Slimfit, co giãn tốt, không bo ống phù hợp mặc đa chức năng\r\nBẢO HÀNH TRỌN ĐỜI khoá kéo YKK, bất kỳ khi nào khoá kéo hỏng Coolmate sẽ đổi cho bạn sản phẩm mới!\r\nNhà cung cấp vải: Saitex\r\nTự hào sản xuất tại Việt Nam', 599000, 1),
(16, 'JEANS', 'Quần Jeans Basic Slimfit xé gối - màu Xanh nhạt', 'blue', '30,31,32', 'Chất liệu: 78% Cotton+ 10% Polyester + 11% Rayon + 1% Spandex\r\n10% Polyester và 11% Rayon đảm bảo tính mềm tay, đứng form\r\n1% Spandex để có co giãn\r\nKiểu dáng Slimfit, không bo ống\r\nTự hào sản xuất tại Việt Nam\r\nNgười mẫu: 175 cm - 69 kg, mặc áo XL, quần size 32', 399000, 1),
(17, 'JEANS', 'Quần Jeans Clean Denim dáng Slimfit', 'light blue', '31,32', 'Vải Denim được dệt toàn bộ tại nhà máy Nhơn Trạch, Đồng Nai của Saitex \r\nChất liệu: 98% Regenerative Cotton (Cotton Tái sinh) + 2% Spandex, tạo sự co giãn đem đến sự thoải mái khi vận động\r\nĐịnh lượng vải nhẹ chỉ với 11.4 Oz\r\nCông nghệ nhuộm KHÔNG sử dụng Sufur (lưu huỳnh) và KHÔNG có nước thải ra môi trường\r\nCông nghệ nhuộm Rope Dyeing mang lại độ bền màu cao hơn cho vải\r\nKiểu dáng Slimfit, không bo ống phù hợp mặc đa chức năng\r\nBẢO HÀNH TRỌN ĐỜI khoá kéo YKK, bất kỳ khi nào khoá kéo hỏng Coolmate sẽ đổi cho bạn sản phẩm mới!\r\nHDSD: Tránh ngâm trong nước quá lâu - Tránh tiếp xúc với các chất liệu gây loang màu - Không sử dụng chất tẩy, tránh giặt bằng máy', 449000, 1),
(18, 'JEANS', 'Coolmate x Copper Denim | Quần Jeans OG Slim', 'dark blue,light blue', '29,30,31,32,33', 'Chất liệu: 12 Oz / 99% Cotton - 1% Spandex\r\nDáng OG Slim: Dáng ôm tôn dáng, ống quần từ phần đùi xuống gấu của OG là rộng hơn Slim fit\r\nTính chất: Không còn rút và hạn chế ra màu sau khi giặt.\r\nCảm giác khi chạm mịn màng\r\nLưu ý:Sản phẩm vẫn sẽ bạc màu sau một thời gian dài sử dụng theo tính chất tự nhiên\r\nNgười mẫu: 175 cm - 69 kg, mặc áo XL, quần size 32\r\nTự hào sản xuất tại Việt Nam', 599000, 1),
(19, 'BACKPACK', 'HARD MODE SS2 BACKPACK', 'black', 'standard', '', 259000, 1),
(20, 'WALLET', 'STICKER WALLET', 'white', 'standard', '- Chất liệu : 100% polyester\r\n- Hoạ tiết : over print ( In toàn thân vải )\r\n- Kích thước : 13.5cm  x 8.5cm ( chiều ngang - chiều cao )\r\n- Logo thương hiệu được in đính ở mặt vải bên trong\r\n\r\nƯu điểm :\r\n- Dây đeo dài tiện dụng\r\n- Gồm nhiều ngăn nhỏ đựng thẻ ngân hàng, giấy tờ và thẻ căn cước\r\n- Bên trong là một ngăn lớn với sức chứa vừa phải\r\n- Mặt sau được trang trí bởi một ngăn lưới nhựa để nhấn mạnh tính thẩm mỹ của chiếc ví', 69000, 1);

